    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:tambah
        try {
            String Tipe = "";
            if (ekonomi.isSelected()){
                Tipe = ekonomi.getText();
            }else if (Bisnis.isSelected()){
                Tipe = Bisnis.getText();
            }else{
                Tipe = eksekutif.getText();
            }
            String sql = "INSERT INTO jadwal Values ('" + maskapai.getSelectedItem()
                    + "','" + Dari.getSelectedItem() + "','"
                    + Menuju.getSelectedItem() + "','" + berangkat.getText()
                    + "','" + Tiba.getText() + "','"+Tipe
                    +"','"+Harga.getText()+"')";
            java.sql.Connection con = (Connection) cone.configDB();
            java.sql.PreparedStatement pst = con.prepareStatement(sql);
            pst.execute();
            JOptionPane.showMessageDialog(null, "Penyimpanan data berhasil");
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, e.getMessage());
        }
        kosong();
        loadtable();
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:ubah
        try {
            String Tipe = "";
            if (ekonomi.isSelected()){
                Tipe = ekonomi.getText();
            }else if (Bisnis.isSelected()){
                Tipe = Bisnis.getText();
            }else{
                Tipe = eksekutif.getText();
            }
            String sql = "UPDATE jadwal SET Maskapai = '" + maskapai.getSelectedItem()
                    + "',Dari ='" + Dari.getSelectedItem() + "',Menuju = '"
                    + Menuju.getSelectedItem() + "',Berangkat = '" + berangkat.getText()
                    + "',Tiba = '" + Tiba.getText() + "',Tipe = '"+Tipe
                    +"',Harga = '"+Harga.getText()+"' WHERE Tiba = '"+Tiba.getText()+"'and "
                    + "Berangkat = '"+berangkat.getText()+"'";
            java.sql.Connection con = (Connection) cone.configDB();
            java.sql.PreparedStatement pst = con.prepareStatement(sql);
            pst.execute();
            JOptionPane.showMessageDialog(null, "Data berhasil di edit");
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, " Perubahan data gagal" + e.getMessage());
        }
        loadtable();
        kosong();
    }//GEN-LAST:event_jButton2ActionPerformed

