    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
        // TODO add your handling code here:
        int baris = jTable1.rowAtPoint(evt.getPoint());
        String mas = jTable1.getValueAt(baris, 1).toString();
        maskapai.setSelectedItem(mas);
        String dar = jTable1.getValueAt(baris, 2).toString();
        Dari.setSelectedItem(dar);
        String jr = jTable1.getValueAt(baris, 3).toString();
        Menuju.setSelectedItem(jr);
        String al = jTable1.getValueAt(baris, 4).toString();
        berangkat.setText(al);
        String tel = jTable1.getValueAt(baris, 5).toString();
        Tiba.setText(tel);
        String tip = jTable1.getValueAt(baris, 6).toString();
        if (tip.equalsIgnoreCase("Ekonomi")){
            ekonomi.setSelected(true);
        }else if (tip.equalsIgnoreCase("Bisnis")){
            Bisnis.setSelected(true);
        }else{
            eksekutif.setSelected(true);
        }
        String har = jTable1.getValueAt(baris, 7).toString();
        Harga.setText(har);
        
    }//GEN-LAST:event_jTable1MouseClicked

